import { useEffect, useState } from 'react';
import ApiRoutes from '../../ApiRoutes';
import styles from './folder_structure.module.css';

export default function FolderStructure(props) {
	const [templates, setTemplates] = useState([]);
	const [template, setTemplate] = useState('');
	const [folderName, setFolderName] = useState('');

	useEffect(() => {
		prepareTemplateList();
	}, []);

	function prepareTemplateList() {
		fetch(ApiRoutes.templateList)
			.then((res) => res.json())
			.then((data) => {
				const templateList = data.templateList;
				setTemplates(templateList);
				props.setSelectedTemplate(templateList[0].value);
			});
	}

	function setSelectedTemplate(selectedTemplate) {
		setTemplate(selectedTemplate);
	}

	function createFromTemplate() {
		fetch(
			ApiRoutes.folderStructure +
				'?' +
				new URLSearchParams({
					rootName: folderName,
					templateName: template,
				})
		).then(() => window.location.replace('/'));
	}

	return (
		<>
			<div className={styles.select}>
				<select onChange={(e) => setTemplate(e.target.value)}>
					{templates.map((template, index) => {
						return (
							<option value={template.value} key={index}>
								{template.label}
							</option>
						);
					})}
				</select>
			</div>

			<input
				className={styles.input}
				placeholder="Enter project name"
				value={folderName}
				onChange={(e) => setFolderName(e.target.value)}
			/>

			<button className={styles.button} onClick={createFromTemplate}>
				Create from template
			</button>
		</>
	);
}
